import React, { useEffect, useState } from "react";
import { useDispatch, useSelector } from "react-redux";
import { useNavigate } from "react-router-dom";
import { getEmployerProfile } from "../../../actions/EmployerActions";
import { getEmployeeProfile } from "../../../actions/EmplyeeActions";
import ActiveJobs from "../../../components/EmployerComponents/MyJobsComps/ActiveJobs";
import AllJobs from "../../../components/EmployerComponents/MyJobsComps/AllJobs";

const EmployeeJobsPage = () => {
  const navigate = useNavigate();
  const dispatch = useDispatch();
  const user = useSelector((state) => state.user);
  const employerData = useSelector((state) => state.employerData);

  console.log(employerData);

  const [ed, setEd] = useState("");
  const [keyword, setKeyword] = useState("");

  useEffect(() => {
    
    dispatch(getEmployeeProfile());
  }, [user]);

  const completedJobs = employerData?.completedJobs?.filter((el) => {
    return el.status === "completed";
  });

  return (
    <div className="postJobs">
      <div className="post-box">
        <div className="header">
          <button
            style={
              ed === "active"
                ? { backgroundColor: "#1d4354", color: "white" }
                : {}
            }
            onClick={() => setEd("active")}
          >
            Active Jobs
          </button>
          <button
            style={
              ed === "completed"
                ? { backgroundColor: "#1d4354", color: "white" }
                : {}
            }
            onClick={() => setEd("completed")}
          >
            Completed Jobs
          </button>
          <button
            style={
              ed === "running"
                ? { backgroundColor: "#1d4354", color: "white" }
                : {}
            }
            onClick={() => setEd("running")}
          >
            On going Jobs
          </button>
          <button
            style={
              ed === "archive"
                ? { backgroundColor: "#1d4354", color: "white" }
                : {}
            }
            onClick={() => setEd("archive")}
          >
            Archived Jobs
          </button>
        </div>
        <form className="jobs-search">
          <input
            style={{ width: "81%" }}
            type="text"
            onChange={(e) => setKeyword(e.target.value)}
          />
          <button type="submit">Search</button>
        </form>

        <div className="main-body">
          {ed === "active" ? (
            <ActiveJobs />
          ) : ed === "completed" ? (
            <AllJobs jobs={completedJobs} />
          ) : ed === "running" ? (
            "<RunningJobs jobs={runningJobs} />"
          ) : ed === "archive" ? (
            " <CancelledJobs jobs={archiveJobs} />"
          ) : (
            ""
          )}
        </div>
      </div>
    </div>
  );
};

export default EmployeeJobsPage;
