import React, { useEffect, useState } from "react";
import { useDispatch, useSelector } from "react-redux";
import { getAllKyc } from "../../../actions/adminActions";
import AdminAcceptedKyc from "../../../components/AdminKyc/AdminAcceptedKyc";
import AdminAllKyc from "../../../components/AdminKyc/AdminAllKyc";
import AdminRejected from "../../../components/AdminKyc/AdminRejected";
import "./AdminKyc.css";

const AdminKyc = () => {
  const dispatch = useDispatch();
  const kyc = useSelector((state) => state.allKyc);
  const kycStatus = useSelector(state => state.kycReq)

  const kycRequest = kyc?.data?.filter(el => {
    console.log(el);
    return el.kycStatus === 'pending'
  })

  const rejectedKyc = kyc?.data?.filter(el => {
    console.log(el);
    return el.kycStatus === 'rejected'
  })

  const acceptedKyc = kyc?.data?.filter(el => {
    console.log(el);
    return el.kycStatus === 'accepted'
  })

  console.log(acceptedKyc);

  const [ed, setEd] = useState("kycReq");

  console.log(kyc);

  useEffect(() => {
    dispatch(getAllKyc());
  }, []);

  return (
    <div className="kyc">

      <div className="kyc-box">
        {" "}
        <div className="top">
          <button className="bnt" onClick={() => setEd("kycReq")}>
            Kyc request
          </button>
          <button className="bnt" onClick={() => setEd("acceptedKyc")}>
            Accepted Kyc
          </button>
          <button className="bnt" onClick={() => setEd("block")}>
            Rejected Kyc
          </button>
        </div>
        <div className="bottom">
          {ed === "kycReq" ? (
            <AdminAllKyc kycRequest={kycRequest} />
          ) : ed === "acceptedKyc" ? (
            
               <AdminAcceptedKyc acceptedRequest={acceptedKyc}/> 
            
          ) : ed === "block" ? (
            <AdminRejected rejectedRequest={rejectedKyc}/> 
          ) : (
            ""
          )}
        </div>
      </div>
    </div>
  );
};

export default AdminKyc;
