import { axiosJobsInstance } from "../contants/axios";
import {MYJOBS_FAIL,MYJOBS_REQUEST,MYJOBS_SUCCES} from '../contants/jobsContants.js'



export const myJobs = (keyword = '') => async (dispatch) => {
    try {
      dispatch({
        type: MYJOBS_REQUEST,
      });
  
      const tokenId = JSON.parse(localStorage.getItem("userInfo"));
  
      const config = {
        headers: {
          "Content-Type": "application/json",
          Authorization: `Bearer ${tokenId.token}`,
        },
      };
  
      const { data } = await axiosJobsInstance.get(`/mypost/${tokenId._id}?keyword${keyword}`, config);

      console.log(data);
  
      dispatch({
        type: MYJOBS_SUCCES,
        payload: data,
      });
    } catch (error) {
      dispatch({
        type: MYJOBS_FAIL,
        error: error,
      });
    }
  };
