import React, { useState } from "react";
import { useDispatch, useSelector } from "react-redux";
import { Link } from "react-router-dom";
import { logout } from "../../actions/UserAction";
import "./header.css";
import Dropdown from "react-bootstrap/Dropdown";

const Header = () => {
  const dispatch = useDispatch();

  const user = useSelector((state) => state.user);
  const [drop, setDrop] = useState(false)
  console.log(user?.userInfo?.userType);

  const handleLogout = () => {
    dispatch(logout());
  };

  return (
    <div style={{ width: "100%" }}>
      <header>
        <div className="left">
          <Link style={{ color: "white", textDecoration: "none" }} to="/">
            <h2>GETWORKER</h2>
          </Link>
        </div>

        {user?.userInfo?._id ? (
          <div className="right big-nav">
            {user?.userInfo?.userType === "employee" ? (
              <>
                <Link style={{ marginRight: "45px" }} to="/myjobs ">
                  My Jobs
                </Link>
                <Link style={{ marginRight: "45px" }} to="/earnings ">
                  Earnings
                </Link>
                <Link style={{ marginRight: "45px" }} to="/findJobs ">
                  Find talents
                </Link>
                <Link to="/message ">FindJobs</Link>
              </>
            ) : user?.userInfo?.userType === "employer" ? (
              <>
                <Link style={{ marginRight: "45px" }} to="/findTalents ">
                  Find talents
                </Link>
                <Link to="/message ">message</Link>
              </>
            ) : (
              <>
                <Link style={{ marginRight: "45px" }} to="admin/users">
                  Users
                </Link>
                <Link style={{ marginRight: "45px" }} to="admin/withdraw">
                  Withdraw request
                </Link>
                <Link style={{ marginRight: "45px" }} to="admin/kyc">
                  Kyc
                </Link>
                <Link to="/message ">message</Link>
              </>
            )}

              <button
                className="btn"
                style={{ backgroundColor: "transparent", color: " #3CCF4E" }}
                id="dropdown-basic"
                onClick={() => setDrop(true)}
              >
                {user?.userInfo?.name}
              </button>
              

              <Dropdown.Menu>
                <Dropdown.Item>
                  {" "}
                  <Link
                    to={
                      user?.userInfo?.userType === "employee"
                        ? "user/profile"
                        : user?.userInfo?.userType === "employer"
                        ? "employer/profile"
                        : "admin/profile"
                    }
                    style={{ color: "#212529" }}
                  >
                    profile
                  </Link>
                </Dropdown.Item>
                <Dropdown.Item href="#/action-2">Another action</Dropdown.Item>
                <Dropdown.Item onClick={handleLogout}>LogOut</Dropdown.Item>
              </Dropdown.Menu>
          </div>
        ) : (
          <div className="right">
            <button className="btn-1">
              <Link
                style={{ color: "black", textDecoration: "none" }}
                to="/signup"
              >
                SignUp
              </Link>
            </button>
            <button className="btn-2">
              <Link
                style={{ color: "white", textDecoration: "none" }}
                to="/login"
              >
                Login
              </Link>
            </button>
          </div>
        )}
      </header>
    </div>
  );
};

export default Header;
