import React, { useState } from "react";
import "./EmployeeConnects.scss";
import {useDispatch} from 'react-redux'
import { checkout } from "../../../actions/paymentActions";

const EmployeeConnects = () => {

  const dispatch = useDispatch();

  const [amount, setAmount] = useState('');

 
  var rzp1 = new window.Razorpay("options");
  rzp1.open();


  return (
    <div className="employeeConnects">
      <div>
        <h2>Buy connects</h2>
      </div>

      <div className="row">
        <div className="buy-box">
          <p>100 connects</p>
          <p>
            Buy for <strong>RS: 500.00</strong>
          </p>
          <h3>Basic Pack</h3>
          <button onClick={() => {
            setAmount(500)


            
          }} >Buy Now</button>
        </div>

        <div className="buy-box">
          <p>200 connects</p>
          <p>
            Buy for <strong>RS: 1000.00</strong>
          </p>
          <h3>Basic Pack</h3>
          <button onClick={() => {
            setAmount(1000)
            dispatch(checkout(1000))
          }} >Buy Now</button>
        </div>

        <div className="buy-box">
          <p>500 connects</p>
          <p>
            Buy for <strong>RS: 5000.00</strong>
          </p>
          <h3>Basic Pack</h3>
          <button onClick={() => {
            setAmount(5000)
            dispatch(checkout(5000))
          }} >Buy Now</button>
        </div>
      </div>
    </div>
  );
};

export default EmployeeConnects;
