import React, { useEffect, useState } from "react";
import { useDispatch, useSelector } from "react-redux";
import { getAllKyc } from "../../actions/adminActions";
import VisibilityIcon from "@mui/icons-material/Visibility";

const AdminAllKyc = () => {
  const dispatch = useDispatch();
  const kyc = useSelector((state) => state.allKyc);

  const [aathar, setAathar] = useState("");
  const [aatharSelfie, setAatharSelfie] = useState("");
  const [pan, setPan] = useState("");
//   18AABCU9603R1ZM
  const [ed, setEd] = useState(false);


  

  useEffect(() => {
    dispatch(getAllKyc());
  }, []);

  return (
    <div>
      {kyc?.data
        ? kyc?.data?.map((el) => {
            return (
              <div className="allkyc-box">
                <div>
                  <p>{el?.owner?.name}</p>
                  <p style={{ marginTop: "-20px" }}>
                    {" "}
                    <strong>{el?.owner?.email}</strong>
                  </p>
                </div>

                <button
                  onClick={() => {
                    setEd(true);
                    setAathar(el?.aatharImage)
                  }}
                >
                  <VisibilityIcon />
                </button>
              </div>
            );
          })
        : ""}

      {ed ? (
        <div className="admin-kyc-show">
          <div>
            <img src={aathar} alt="" />
            <img src={aatharSelfie} alt="" />
            <img src={pan} alt="" />
          </div>
        </div>
      ) : (
        ""
      )}
    </div>
  );
};

export default AdminAllKyc;
