import React, { useEffect, useState } from "react";
import { useDispatch, useSelector } from "react-redux";
import { getAllKyc } from "../../actions/adminActions";
import VisibilityIcon from "@mui/icons-material/Visibility";
import { logout } from "../../actions/UserAction";

const AdminAllKyc = () => {
  const dispatch = useDispatch();
  const kyc = useSelector((state) => state.allKyc);

  const [aathar, setAathar] = useState("");
  const [aatharSelfie, setAatharSelfie] = useState("");
  const [pan, setPan] = useState("");
  const [gst, setGst] = useState("");
//   
  const [ed, setEd] = useState(false);

  console.log(aathar);

//   dispatch(logout())

  useEffect(() => {
    dispatch(getAllKyc());
  }, []);

  return (
    <div>
      {kyc?.data
        ? kyc?.data?.map((el) => {
            return (
              <div className="allkyc-box">
                <div>
                  <p>{el?.owner?.name}</p>
                  <p style={{ marginTop: "-20px" }}>
                    {" "}
                    <strong>{el?.owner?.email}</strong>
                  </p>
                </div>

                <button
                  onClick={() => {
                    setEd(true);
                    setAathar(el?.aatharImage)
                    setAatharSelfie(el?.aatharSelfie)
                    setPan(el?.panImage)
                    setGst(el?.gstNumber)
                  }}
                >
                  <VisibilityIcon />
                </button>
              </div>
            );
          })
        : ""}

      {ed ? (
        <div className="admin-kyc-show">
          <div  >
            <img src={aathar} alt="" />
            <img src={aatharSelfie} alt="" />
            <img src={pan} alt="" />
            <p style={{marginTop: '10px', color: 'white'}} >{gst}</p>
          </div>

          <div style={{display: fle}} >
          <button>Accept</button>
          <button>Reject</button>
          </div>
        </div>
      ) : (
        ""
      )}
    </div>
  );
};

export default AdminAllKyc;
