                return (
                  <>
                    <ul style={{ listStyle: "none" }}>
                      <li
                        style={{
                          width: "430px",
                          display: "flex",
                          justifyContent: "space-between",
                        }}
                      >
                      {skill?.skill}
                        <button
                          onClick={() => {
                           dispatch(deleteLanguageOrSkill('', skill?.skill))
                          }}
                          className="deleteIcon"
                        >
                          <DeleteIcon />{" "}
                        </button>{" "}
                      </li>
                    </ul>
                  </>
                );
              })}
