import {
    JOBS_DETAILS_REQUEST,
  JOBS_DETAILS_SUCCES,
  MYJOBS_FAIL,
  MYJOBS_REQUEST,
  MYJOBS_SUCCES,
} from "../contants/jobsContants";

export const MyJobsReducer = (state = {}, action) => {
  switch (action.type) {
    case MYJOBS_REQUEST:
      return { loading: true };

    case MYJOBS_SUCCES:
      return { loading: false, myJobs: action.payload };

    case MYJOBS_FAIL:
      return { loading: false, error: action.error };

    default:
      return state;
  }
};


export const jobsDetailsReducer = (state = {}, action) => {
    switch (action.type) {
      case JOBS_DETAILS_REQUEST:
        return { loading: true };
  
      case JOBS_DETAILS_SUCCES:
        return { loading: false, myJobs: action.payload };
  
      case MYJOBS_FAIL:
        return { loading: false, error: action.error };
  
      default:
        return state;
    }
  };
