import React, { useEffect, useState } from "react";
import { useDispatch, useSelector } from "react-redux";

import DeleteIcon from "@mui/icons-material/Delete";
import EditIcon from "@mui/icons-material/Edit";
import DoNotDisturbOnIcon from "@mui/icons-material/DoNotDisturbOn";
import Spinner from "react-bootstrap/Spinner";
import {
  addEducation,
  deleteEducation,
  getEmployeeProfile,
} from "../../actions/EmplyeeActions";
import { changePassword } from "../../actions/UserAction";

const ChangePasswordPopup = () => {
  const dispatch = useDispatch();

  const userProfile = useSelector((state) => state.employeeData);
  const educationReq = useSelector((state) => state.education);
  const changePasswordR = useSelector((state) => state.changePasswords);
  const [oldPassword, setOldPassword] = useState("");
  const [newPassword, setNewPassword] = useState("");
  const [confirmPassword, setConfirmPassword] = useState("");

  const { userData } = userProfile;

  console.log(changePasswordR?.message);

  return (
    <div>
      <div className="educationPopUp">
        <p
          className="common-heading"
          style={{ textAlign: "center", marginLeft: "-30px" }}
        >
          Change Password
        </p>

        <form
          onSubmit={(e) => {
            e.preventDefault();
            dispatch(changePassword(oldPassword, newPassword ));
          }}
        >
          <input
            className="n"
            type="text"
            onChange={(e) => setOldPassword(e.target.value)}
            placeholder="Old Password"
          />
          <input
            type="text"
            onChange={(e) => setNewPassword(e.target.value)}
            placeholder="New Password"
          />

          <input
            type="text"
            onChange={(e) => setConfirmPassword(e.target.value)}
            placeholder="Confirm Password"
          />
          {changePasswordR?.loading ? <div style={{display:'flex', justifyContent: 'center', marginBottom: '40px'}}><Spinner animation="border" role="status"></Spinner></div> : ''}
          {changePasswordR?.message ? <p cla >{changePasswordR?.message}</p> : ''}
          <button type="submit">Add</button>
        </form>
      </div>
    </div>
  );
};

export default ChangePasswordPopup;
