import React, { useState } from "react";
import { useSelector } from "react-redux";
import ActiveJobs from "../../../components/EmployerComponents/MyJobsComps/ActiveJobs";

const EmployeeJobsPage = () => {

    
  const employerData = useSelector((state) => state.employerData);

  console.log(employerData);

  const [ed, setEd] = useState("");
  const [keyword, setKeyword] = useState("");

  useEffect(() => {
    if (!user?.userInfo) {
      navigate("/login");
    }
    if (user?.userInfo?.userType === "employee") {
      navigate("/user/home");
    }
    if (user?.userInfo?.userType === "admin") {
      navigate("/admin/profile");
    }

    dispatch(getEmployerProfile());
  }, [user]);
  let activeJobs

  return (
    <div className="postJobs">
      <div className="post-box">
        <div className="header">
          <button
            style={
              ed === "active"
                ? { backgroundColor: "#1d4354", color: "white" }
                : {}
            }
            onClick={() => setEd("active")}
          >
            Active Jobs
          </button>
          <button
            style={
              ed === "completed"
                ? { backgroundColor: "#1d4354", color: "white" }
                : {}
            }
            onClick={() => setEd("completed")}
          >
            Completed Jobs
          </button>
          <button
            style={
              ed === "running"
                ? { backgroundColor: "#1d4354", color: "white" }
                : {}
            }
            onClick={() => setEd("running")}
          >
            On going Jobs
          </button>
          <button
            style={
              ed === "archive"
                ? { backgroundColor: "#1d4354", color: "white" }
                : {}
            }
            onClick={() => setEd("archive")}
          >
            Archived Jobs
          </button>
        </div>
        <form className="jobs-search">
          <input
            style={{ width: "81%" }}
            type="text"
            onChange={(e) => setKeyword(e.target.value)}
          />
          <button type="submit">Search</button>
        </form>

        <div className="main-body">
          {ed === "active" ? (
            <ActiveJobs jobs={activeJobs} />
          ) : ed === "completed" ? (
            "<AllJobs jobs={completedJobs} />"
          ) : ed === "running" ? (
            "<RunningJobs jobs={runningJobs} />"
          ) : ed === "archive" ? (
           " <CancelledJobs jobs={archiveJobs} />"
          ) : (
            ""
          )}
        </div>
      </div>
    </div>
  );
};

export default EmployeeJobsPage;
