import React, { useState } from "react";
import "./EmployeeConnects.scss";
import {useDispatch} from 'react-redux'
import { checkout } from "../../../actions/paymentActions";

const EmployeeConnects = () => {

  const dispatch = useDispatch();

  const [amount, setAmount] = useState('');

 
  var rzp1 = new window.Razorpay("options");
  rzp1.open();


  return (
    <div className="employeeConnects">
      <div>
        <h2>Buy connects</h2>
      </div>

      <div className="row">
        <div className="buy-box">
          <p>100 connects</p>
          <p>
            Buy for <strong>RS: 500.00</strong>
          </p>
          <h3>Basic Pack</h3>
          <button onClick={ async() => {
            setAmount(500)


            const {
      data: { key },
    } = await axios.get("http://localhost:3001/api/getkey");

    const { data } = await axios.post("http://localhost:3001/api/checkout ", {
      amount: "1000",
    });

    
    const options = {
      key, // Enter the Key ID generated from the Dashboard
      amount: data?.amount, // Amount is in currency subunits. Default currency is INR. Hence, 50000 refers to 50000 paise
      currency: "INR",
      name: "Get worker",
      description: "Buy connects",
      image: "https://example.com/your_logo",
      order_id: data?.id, //This is a sample Order ID. Pass the `id` obtained in the response of Step 1
      callback_url: "http://localhost:3001/api/paymentVerification",
      prefill: {
        name: "Gaurav Kumar",
        email: "gaurav.kumar@example.com",
        contact: "9999999999",
      },
      notes: {
        address: "Razorpay Corporate Office",
      },
      theme: {
        color: "#3399cc",
      },
    };
    var rzp1 = new window.Razorpay(options);
    rzp1.open();

            
          }} >Buy Now</button>
        </div>

        <div className="buy-box">
          <p>200 connects</p>
          <p>
            Buy for <strong>RS: 1000.00</strong>
          </p>
          <h3>Basic Pack</h3>
          <button onClick={() => {
            setAmount(1000)
            dispatch(checkout(1000))
          }} >Buy Now</button>
        </div>

        <div className="buy-box">
          <p>500 connects</p>
          <p>
            Buy for <strong>RS: 5000.00</strong>
          </p>
          <h3>Basic Pack</h3>
          <button onClick={() => {
            setAmount(5000)
            dispatch(checkout(5000))
          }} >Buy Now</button>
        </div>
      </div>
    </div>
  );
};

export default EmployeeConnects;
