import React, { useEffect, useState } from "react";
import { useDispatch, useSelector } from "react-redux";
import { Link, useParams } from "react-router-dom";
import { viewProposals } from "../../../actions/proposalActions";
import "./ViewProposal.css";

const ViewProposal = () => {
    const dispatch = useDispatch();
    const viewproposal = useSelector((state) => state.viewProposal)
    const proposalData = viewProposals
    const {id} = useParams();
  useEffect(() => {
    dispatch(viewProposals(id))
  }, []);

  console.log(viewproposal);

  const jobsInfo = []

  const [ed, setEd] = useState('');

  return <div className="jobsDetails-view">
  <div className="job-details-box">
    <div className="top">
      <h4>{jobsInfo?.title}</h4>
    </div>

    <div className="bottom">
      <p>{jobsInfo?.description}</p>

      <div className="row">
        <p>
          Budget: <strong>${jobsInfo?.budget}</strong>
        </p>
        <p>
          Deadline: <strong>{jobsInfo?.deadline} days</strong>
        </p>
        <p>
          Difficulty:{" "}
          <strong>
            {jobsInfo?.level === "advanced" ? (
              <span style={{ color: "#FF9393" }}>{jobsInfo?.level}</span>
            ) : (
              <span style={{ color: "#3ccf4e" }}>{jobsInfo?.level}</span>
            )}
          </strong>{" "}
        </p>
      </div>

      <Link to={`/jobs/${jobsInfo?._id}/proposal`}>
        <button>Submit Proposal</button>
      </Link>
    </div>
  </div>

  <div style={{width: '100%'}}>
  <div className="postJobs">
  <div className="post-box">
    <div className="header">
      <button
        style={
          ed === "active"
            ? { backgroundColor: "#1d4354", color: "white" }
            : {}
        }
        onClick={() => setEd("active")}
      >
        Active Proposals
      </button>
      <button
        style={
          ed === "rejected"
            ? { backgroundColor: "#1d4354", color: "white" }
            : {}
        }
        onClick={() => setEd("rejected")}
      >
        Rejected Proposals
      </button>
      <button
        style={
          ed === "shortlisted"
            ? { backgroundColor: "#1d4354", color: "white" }
            : {}
        }
        onClick={() => setEd("shortlisted")}
      >
        ShortListed Proposals
      </button>
    </div>
    {/* <form  className="jobs-search">
      <input
        style={{ width: "81%" }}
        type="text"
        onChange={(e) => setKeyword(e.target.value)}
      />
      <button type="submit">Search</button>
    </form> */}

    <div className="main-body">
    
      {/* {ed === "active" ? (
        <ProposalComponent proposals={activeProposals} />
      ) : ed === "rejected" ? (
        <ProposalComponent proposals={rejectedProposals} />
      ) : ed === "shortlisted" ? (
        <ProposalComponent proposals={shortLitedProposals} />
      ) : (
        ""
      )} */}

    </div>
  </div>
</div>
</div>
</div>;
};

export default ViewProposal;
