import React, { useState } from "react";
import { useSelector } from "react-redux";
import "./PostJobs.css";

const PostJobs = () => {
  const [title, setTitle] = useState("");
  const [description, setDescription] = useState("");
  const [budget, setBudget] = useState("");
  const [level, setLevel] = useState('');
  const [tags, setTags] = useState([]);


  return (
    <div className="postJobs">
      <div className="post-box">
        <div className="top">
          <h4>Get started with your job post</h4>
        </div>

        <div className="bottom">
          <input
            type="text"
            placeholder="Title for your job post"
            name=""
            id=""
          />
          <textarea
            name=""
            placeholder="Add your detailed decription about your job"
            id=""
            cols="30"
            rows="10"
          ></textarea>

          <div className="row">
            <input type="text" placeholder="Add Search tags" />{" "}
            <button>Add</button>
          </div>

          <div className="row1">
            <p>ghfg</p>
            <p>dfgdg</p>
          </div>

          <div className="row">
            <input type="number" placeholder="$Budget" name="" id="" />
            <input type="date" name="" id="" />

            <select onClick={(e) => {
                console.log(e.target.value)
            }} name="cars" id="cars">
              <option value="easy">Easy</option>
              <option value="intermediate">Intermediate</option>
              <option value="advanced">Advanced</option>
            </select>
          </div>
          <button>Post</button>
        </div>
      </div>
    </div>
  );
};

export default PostJobs;
