import axios from "axios";
import {
  ALL_EMPLYEES_FAIL,
  ALL_EMPLYEES_REQUEST,
  ALL_EMPLYEES_SUCCESS,
} from "../contants/adminConstants";

export const getAllEmplyees =
  (keyword = "") =>
  async (dispatch) => {
    try {
      dispatch({
        type: ALL_EMPLYEES_REQUEST,
      });

      const id = JSON.parse(localStorage.getItem("userInfo"));

      const config = {
        headers: {
          "Content-Type": "application/json",
          Authorization: `Bearer ${id.token}`,
        },
      };

      const { data } = await axios.get(
        `/api/admin/allEmployees?keyword=${keyword}`,
        config
      );

      console.log(data);

      dispatch({
        type: ALL_EMPLYEES_SUCCESS,
        payload: data,
      });
    } catch (error) {
      dispatch({
        type: ALL_EMPLYEES_FAIL,
        error: error,
      });
    }
  };

export const blockUser = (_id) => async (dispatch, getState) => {
  try {
    const tokenId = JSON.parse(localStorage.getItem("userInfo"));

    const config = {
      headers: {
        "Content-Type": "application/json",
        Authorization: `Bearer ${tokenId.token}`,
      },
    };

    const { datad } = await axios.patch(
      `http://localhost:3001/api/admin/block/${_id}`,
      config
    );

    const { data } = await axios.get(`/api/admin/allEmployees`, config);

    dispatch({
      type: ALL_EMPLYEES_SUCCESS,
      payload: data,
    });
  } catch (error) {
    dispatch({
      type: ALL_EMPLYEES_FAIL,
      error: error,
    });
  }
};
