import React, { useEffect, useState } from "react";
import { Link, useNavigate, useParams } from "react-router-dom";
import ChatWindow from "../../../components/ChatWindow/ChatWindow";
import axios from "axios";
import {v4 as uuidv4} from 'uuid'

const EmployeeMessage = ({ socket }) => {


    const navigate = useNavigate();
    const [rooms, setRooms] = useState([]);
  
    const handleClick = () => {
      const roomId = uuidv4();
      navigate(`/room/${roomId}`);
      socket.emit("new-room-created", { roomId });
      setRooms([...rooms, roomId]);
    };
  
    useEffect(() => {
      async function fetchRooms() {
        const { data} = await axios.get("http://localhost:3001/rooms");
        const {rooms} =  data
        console.log(rooms);
        setRooms(rooms)
      }
  
      fetchRooms();
    }, []);
  
    useEffect(() => {
      if (!socket) return;
  
      socket.on("new-room-created", ({ roomId }) => {
        setRooms([...rooms, roomId]);
      });
    }, [socket]);
  

  return (
    <div>

<div
        style={{ display: "flex", flexDirection: "column", marginTop: "20px" }}
      >
        {rooms?.map((room) => {
          return <Link to={`user/message/${room?.id}`}>{room?.id}</Link>;
        })}
      </div>
      <p onClick={handleClick}>New Room</p>

      
    </div>
  );
};

export default EmployeeMessage;
