import "./AdminKyc.css";

const AdminKyc = () => {
  const dispatch = useDispatch();
  const kyc = useSelector((state) => state.allKyc);

  const kycRequest = kyc?.data?.filter(el => {
    console.log(el);
    return el.kycStatus === 'rejected'
  })

  console.log(kycRequest);

  const [ed, setEd] = useState("kycReq");

  console.log(kyc);

  useEffect(() => {
    dispatch(getAllKyc());
  }, []);

  return (
    <div className="kyc">

      <div className="kyc-box">
        {" "}
        <div className="top">
          <button className="bnt" onClick={() => setEd("kycReq")}>
            Kyc request
          </button>
          <button className="bnt" onClick={() => setEd("acceptedKyc")}>
            Accepted Kyc
          </button>
          <button className="bnt" onClick={() => setEd("block")}>
            Rejected Kyc
          </button>
        </div>
        <div className="bottom">
          {ed === "kycReq" ? (
            <AdminAllKyc kycRe />
          ) : ed === "employer" ? (
            {
              /* <AllEmployers /> */
            }
          ) : ed === "block" ? (
            {
              /* <BlockedUsers /> */
            }
          ) : (
            ""
          )}
        </div>
      </div>
    </div>
  );
};

export default AdminKyc;
