import React, { useState } from "react";
import { useSelector } from "react-redux";
import ActiveJobs from "../../../components/EmployerComponents/MyJobsComps/ActiveJobs";

const MyPosts = () => {

    const [ed, setEd] = useState('active');
    
  const myJobsData = useSelector((state) => state.myJobs);

  
  useEffect(() => {
    if (!user?.userInfo) {
      navigate("/login");
    }
    if (user?.userInfo?.userType === "employee") {
      navigate("/user/home");
    }
    if (user?.userInfo?.userType === "admin") {
      navigate("/admin/profile");
    }

    dispatch(getEmployerProfile());
    dispatch(myJobs());
  }, [user, navigate, dispatch]);

  return (
    <div className="postJobs">
      <div className="post-box">
        <div className="header">
          <button onClick={() => setEd("active")}>Active Jobs</button>
          <button onClick={() => setEd("all")}>All Jobs</button>
          <button onClick={() => setEd("archive")}>Archived Jobs</button>
        </div>

        <div className="main-body">
        {ed === "active" ? (
          <ActiveJobs jobs={myJobsData} />
          ) : ed === "all" ? (
            "<AdminAcceptedKyc acceptedRequest={acceptedKyc} />"
          ) : ed === "archive" ? (
           " <AdminRejected rejectedRequest={rejectedKyc} />"
          ) : (
            ""
          )}
        </div>
      </div>
    </div>
  );
};

export default MyPosts;
